table.js:
console.log("{{模块名称}}表格模块已加载");

document.addEventListener('DOMContentLoaded', function() {
  // 表格搜索功能
  const searchInput = document.querySelector('.table-module .search-input');
  const tableRows = document.querySelectorAll('.table-module .data-table tbody tr');

  if (searchInput) {
    searchInput.addEventListener('input', function() {
      const searchTerm = this.value.toLowerCase();

      tableRows.forEach(row => {
        const text = row.textContent.toLowerCase();
        row.style.display = text.includes(searchTerm) ? '' : 'none';
      });
    });
  }

  // 表格筛选功能
  const filterSelect = document.querySelector('.table-module .filter-select');

  if (filterSelect) {
    filterSelect.addEventListener('change', function() {
      const filterValue = this.value;

      if (!filterValue) {
        // 显示所有行
        tableRows.forEach(row => {
          row.style.display = '';
        });
        return;
      }

      // 这里可以根据实际需求实现筛选逻辑
      alert(`已选择筛选条件: ${filterValue}`);
    });
  }

  // 表格操作按钮
  const viewButtons = document.querySelectorAll('.table-module .btn-info');
  const editButtons = document.querySelectorAll('.table-module .btn-primary');
  const deleteButtons = document.querySelectorAll('.table-module .btn-danger');

  viewButtons.forEach(button => {
    button.addEventListener('click', function() {
      const row = this.closest('tr');
      const id = row.cells[0].textContent;
      alert(`查看ID为${id}的项目`);
    });
  });

  editButtons.forEach(button => {
    button.addEventListener('click', function() {
      const row = this.closest('tr');
      const id = row.cells[0].textContent;
      alert(`编辑ID为${id}的项目`);
    });
  });

  deleteButtons.forEach(button => {
    button.addEventListener('click', function() {
      const row = this.closest('tr');
      const id = row.cells[0].textContent;
      if (confirm(`确定要删除ID为${id}的项目吗？`)) {
        alert(`已删除ID为${id}的项目`);
      }
    });
  });
});
